import java.util.Scanner;


public class Tea {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s = new Scanner(System.in);
		int n= s.nextInt();
		int x=0;
		for(int i=0; i<n;i++)
		{
			String sc=s.next();
			if(sc.contains("++") && sc.contains("X"))
			{
				x++;
			}
			else if(sc.contains("--") && sc.contains("X"))
			{
				x--;
			}
		}
		
		System.out.print(x);
		
	}

}
